--// Serviceslocal Players = game:GetService("Players")local RunService = game:GetService("RunService")local Stats = game:GetService("Stats")local TeleportService = game:GetService("TeleportService")local HttpService = game:GetService("HttpService")local LocalPlayer = Players.LocalPlayer--// UI g·ªëclocal screenGui = Instance.new("ScreenGui", game.CoreGui)screenGui.Name = "PHUCMAX_UI"screenGui.ZIndexBehavior = Enum.ZIndexBehavior.Globallocal mainFrame = Instance.new("Frame", screenGui)mainFrame.Size = UDim2.new(1,0,1,0)mainFrame.BackgroundTransparency = 1local uiLayout = Instance.new("UIListLayout", mainFrame)uiLayout.HorizontalAlignment = Enum.HorizontalAlignment.CenteruiLayout.VerticalAlignment = Enum.VerticalAlignment.CenteruiLayout.Padding = UDim.new(0,12)local function createRainbowText(text,size)    local label = Instance.new("TextLabel")    label.Size = UDim2.new(0,500,0,size+20)    label.BackgroundTransparency = 1    label.Text = text    label.Font = Enum.Font.GothamBold    label.TextSize = size    label.TextColor3 = Color3.fromRGB(255,255,255)    label.ZIndex = 10    local gradient = Instance.new("UIGradient", label)    gradient.Rotation = 0    gradient.Color = ColorSequence.new{        ColorSequenceKeypoint.new(0, Color3.fromRGB(255,0,0)),        ColorSequenceKeypoint.new(0.2, Color3.fromRGB(255,255,0)),        ColorSequenceKeypoint.new(0.4, Color3.fromRGB(0,255,0)),        ColorSequenceKeypoint.new(0.6, Color3.fromRGB(0,255,255)),        ColorSequenceKeypoint.new(0.8, Color3.fromRGB(0,0,255)),        ColorSequenceKeypoint.new(1, Color3.fromRGB(255,0,255))    }    task.spawn(function()        while task.wait(0.03) do            gradient.Rotation = (gradient.Rotation + 3) % 360        end    end)    return labelend-- Ch·ªØ UI ch√≠nhlocal title = createRainbowText("PHUCMAX",50); title.Parent = mainFramelocal subtitle = createRainbowText("nhattrai",30); subtitle.Parent = mainFramelocal fpsLabel = createRainbowText("FPS: 0",25); fpsLabel.Parent = mainFramelocal pingLabel = createRainbowText("Ping: 0 ms",25); pingLabel.Parent = mainFrame-- Update FPS + Pinglocal frames,lastTime = 0,tick()RunService.RenderStepped:Connect(function()    frames += 1    local currentTime = tick()    if currentTime - lastTime >= 1 then        fpsLabel.Text = "FPS: "..frames        frames = 0        lastTime = currentTime    endend)RunService.Heartbeat:Connect(function()    local pingStat = Stats.Network.ServerStatsItem["Data Ping"]    if pingStat then        pingLabel.Text = "Ping: "..math.floor(pingStat:GetValue()).." ms"    endend)-- Th√¥ng b√°o g√≥c ph·∫£i d∆∞·ªõilocal notifyFrame = Instance.new("Frame", screenGui)notifyFrame.Size = UDim2.new(0,300,0,200)notifyFrame.Position = UDim2.new(1,-310,1,-260)notifyFrame.BackgroundTransparency = 1local notifyLayout = Instance.new("UIListLayout", notifyFrame)notifyLayout.SortOrder = Enum.SortOrder.LayoutOrdernotifyLayout.Padding = UDim.new(0,5)notifyLayout.VerticalAlignment = Enum.VerticalAlignment.Bottomlocal function Notify(msg)    local note = createRainbowText(msg,20)    note.Parent = notifyFrame    note.Size = UDim2.new(1,0,0,25)    task.delay(2,function() note:Destroy() end)end--// Ch·ª©c nƒÉng 1: Auto Join Marinestask.spawn(function()    repeat wait() until LocalPlayer.PlayerGui:FindFirstChild("ChooseTeam")    pcall(function()        local TeamBtn = LocalPlayer.PlayerGui.ChooseTeam.Container.Marines.Frame.ViewportFrame.TextButton        fireclickdetector(TeamBtn.ClickDetector)        Notify("‚öì Auto Join Marines")    end)end)--// Ch·ª©c nƒÉng 2: Auto t√¨m tr√°i + Teleport + ESP rainbowtask.spawn(function()    repeat wait() until LocalPlayer.Character and LocalPlayer.Character:FindFirstChild("HumanoidRootPart")    local function highlightFruit(fruit)        local part = fruit:FindFirstChildWhichIsA("BasePart")        if part then            local bill = Instance.new("BillboardGui", fruit)            bill.Size = UDim2.new(0,100,0,20)            bill.AlwaysOnTop = true            bill.Adornee = part            local txt = createRainbowText(fruit.Name,18)            txt.Parent = bill            txt.Size = UDim2.new(1,0,1,0)        end    end    while wait(10) do        for _, obj in ipairs(workspace:GetDescendants()) do            if obj:IsA("Tool") and obj:FindFirstChild("Handle") and obj.Name:lower():find("fruit") then                highlightFruit(obj)                LocalPlayer.Character.HumanoidRootPart.CFrame = obj.Handle.CFrame + Vector3.new(0,3,0)                firetouchinterest(LocalPlayer.Character.HumanoidRootPart,obj.Handle,0)                firetouchinterest(LocalPlayer.Character.HumanoidRootPart,obj.Handle,1)                Notify("üçé Found & Picked "..obj.Name)            end        end    endend)--// Ch·ª©c nƒÉng 3: Auto server hop khi h·∫øt tr√°itask.spawn(function()    while wait(60) do        local fruitFound = false        for _, obj in ipairs(workspace:GetDescendants()) do            if obj:IsA("Tool") and obj:FindFirstChild("Handle") and obj.Name:lower():find("fruit") then                fruitFound = true                break            end        end        if not fruitFound then            local servers = HttpService:JSONDecode(game:HttpGet("https://games.roblox.com/v1/games/"..game.PlaceId.."/servers/Public?sortOrder=Asc&limit=100"))            for _,v in pairs(servers.data) do                if v.playing < v.maxPlayers and v.id ~= game.JobId then                    Notify("üîÑ Server Hop...")                    TeleportService:TeleportToPlaceInstance(game.PlaceId,v.id,LocalPlayer)                    break                end            end        end    endend)--// Ch·ª©c nƒÉng 4: FixLag X2local function FixLagX2()    for _, obj in ipairs(workspace:GetDescendants()) do        if obj:IsA("BasePart") then            obj.Material = Enum.Material.SmoothPlastic            obj.Color = Color3.fromRGB(150,150,150)            obj.Reflectance = 0        elseif obj:IsA("ParticleEmitter") or obj:IsA("Trail") or obj:IsA("Beam") then            obj.Enabled = false        end    end    for _, obj in ipairs(workspace:GetDescendants()) do        if obj.Name:match("Leaf") or obj.Name:match("Tree") then            obj:Destroy()        elseif obj:IsA("UnionOperation") or obj:IsA("MeshPart") then            obj.Transparency = 0.9        end    end    local Lighting = game:GetService("Lighting")    Lighting.GlobalShadows = false    Lighting.Brightness = Lighting.Brightness * 0.3    Notify("‚úÖ FixLag X2 Applied")endFixLagX2()--// Ch·ª©c nƒÉng 5: Auto Store Fruit_G.AutoStoreFruit = falsetask.spawn(function()    repeat wait() until LocalPlayer.Character    while wait(1) do        if _G.AutoStoreFruit then            pcall(function()                local backpack = LocalPlayer.Backpack                local char = LocalPlayer.Character                for _, fruitName in pairs({"Bomb Fruit","Spike Fruit","Chop Fruit","Spring Fruit","Kilo Fruit","Smoke Fruit","Spin Fruit","Flame Fruit","Falcon Fruit","Ice Fruit","Sand Fruit","Dark Fruit","Revive Fruit","Diamond Fruit","Light Fruit","Love Fruit","Rubber Fruit","Barrier Fruit","Magma Fruit","Portal Fruit","Quake Fruit","Buddha Fruit","Spider Fruit","Phoenix Fruit","Rumble Fruit","Pain Fruit","Gravity Fruit","Dough Fruit","Shadow Fruit","Venom Fruit","Eagle Fruit","creation Fruit","Lightning Fruit","Mammoth Fruit","T-Rex Fruit","Gas Fruit","Yeti Fruit","Kitsune Fruit","Blade Fruit","Control Fruit","Spirit Fruit","Dragon Fruit","Leopard Fruit"}) do                    local tool = char:FindFirstChild(fruitName) or backpack:FindFirstChild(fruitName)                    if tool then                        game:GetService("ReplicatedStorage").Remotes.CommF_:InvokeServer("StoreFruit", fruitName:gsub(" ","-").."-"..fruitName:gsub(" ","-"), tool)                        Notify("üíæ Stored "..fruitName)                    end                end            end)        end    endend)