-- KIỂM TRA UI ĐÃ LOADif getgenv()._RainbowFluentLoaded then return endgetgenv()._RainbowFluentLoaded = true-- SERVICESlocal TweenService = game:GetService("TweenService")local CoreGui = game:GetService("CoreGui")local Players = game:GetService("Players")local LocalPlayer = Players.LocalPlayer-- GUI CHÍNHlocal gui = Instance.new("ScreenGui", CoreGui)gui.Name = "RainbowFluentUI"gui.ResetOnSpawn = false-- HÀM RAINBOW TEXTlocal function rainbowText(obj)	task.spawn(function()		while obj.Parent do			for h = 0,1,0.01 do				obj.TextColor3 = Color3.fromHSV(h,1,1)				task.wait(0.02)			end		end	end)end-- HÀM RAINBOW STROKElocal function rainbowStroke(obj, thickness)	local stroke = Instance.new("UIStroke", obj)	stroke.Thickness = thickness or 2	task.spawn(function()		while obj.Parent do			for h=0,1,0.01 do				stroke.Color = Color3.fromHSV(h,1,1)				task.wait(0.02)			end		end	end)end-- FRAME CHÍNHlocal main = Instance.new("Frame", gui)main.Size = UDim2.new(0, 400, 0, 500)main.AnchorPoint = Vector2.new(0.5,0.5)main.Position = UDim2.new(0.5,0,0.5,0)main.BackgroundColor3 = Color3.fromRGB(25,25,25)main.ClipsDescendants = truemain.Visible = falsemain.Active = truemain.Draggable = trueInstance.new("UICorner", main).CornerRadius = UDim.new(0,12)rainbowStroke(main)-- NÚT BẬT/TẮT MENUlocal logo = Instance.new("TextButton", gui)logo.Size = UDim2.new(0,60,0,60)logo.Position = UDim2.new(0.5,-30,0,10)logo.Text = "UI"logo.Font = Enum.Font.GothamBoldlogo.TextSize = 18logo.BackgroundColor3 = Color3.fromRGB(40,40,40)Instance.new("UICorner", logo).CornerRadius = UDim.new(1,0)rainbowText(logo)rainbowStroke(logo)logo.MouseButton1Click:Connect(function()	if main.Visible then		local tween = TweenService:Create(main,TweenInfo.new(0.3),{Size=UDim2.new(0,0,0,0)})		tween:Play()		tween.Completed:Wait()		main.Visible=false	else		main.Visible=true		local tween = TweenService:Create(main,TweenInfo.new(0.3,Enum.EasingStyle.Quad),{Size=UDim2.new(0,400,0,500)})		tween:Play()	endend)-- TAB BARlocal tabBar = Instance.new("Frame", main)tabBar.Size = UDim2.new(1,-20,0,50)tabBar.Position = UDim2.new(0,10,0,10)tabBar.BackgroundTransparency = 1-- TAB CONTAINERlocal tabContainer = Instance.new("Frame", main)tabContainer.Size = UDim2.new(1,-20,1,-70)tabContainer.Position = UDim2.new(0,10,0,60)tabContainer.BackgroundTransparency = 1local tabs = {}local currentTab = nillocal function switchTab(name)	for t,v in pairs(tabs) do		v.Visible = (t==name)	endendlocal function createTab(name)	local btn = Instance.new("TextButton", tabBar)	btn.Size = UDim2.new(0,120,1,0)	btn.Text = name	btn.Font = Enum.Font.GothamBold	btn.TextSize = 16	btn.BackgroundColor3 = Color3.fromRGB(40,40,40)	Instance.new("UICorner", btn).CornerRadius=UDim.new(0,8)	rainbowText(btn)	rainbowStroke(btn)	local page = Instance.new("ScrollingFrame", tabContainer)	page.Size = UDim2.new(1,0,1,0)	page.BackgroundTransparency = 1	page.CanvasSize = UDim2.new(0,0,0,0)	page.ScrollBarThickness = 4	local layout = Instance.new("UIListLayout",page)	layout.Padding=UDim.new(0,6)	layout.SortOrder = Enum.SortOrder.LayoutOrder	layout.HorizontalAlignment=Enum.HorizontalAlignment.Center	page.Visible=false	btn.MouseButton1Click:Connect(function()		switchTab(name)	end)	tabs[name]=page	if not currentTab then		currentTab=name		page.Visible=true	end	return pageend-- TẠO TAB DEMOlocal tab1 = createTab("Demo1")local tab2 = createTab("Demo2")-- HÀM TẠO NÚTlocal function createButton(parent,text,callback)	local btn = Instance.new("TextButton", parent)	btn.Size = UDim2.new(0.9,0,0,36)	btn.Text = text	btn.Font = Enum.Font.GothamBold	btn.TextSize = 14	btn.BackgroundColor3 = Color3.fromRGB(40,40,40)	Instance.new("UICorner", btn).CornerRadius=UDim.new(0,8)	rainbowText(btn)	rainbowStroke(btn)	btn.MouseButton1Click:Connect(callback)end-- HÀM TẠO TOGGLElocal function createToggle(parent,text,callback)	local holder = Instance.new("Frame",parent)	holder.Size=UDim2.new(0.9,0,0,32)	holder.BackgroundTransparency=1	local box = Instance.new("TextButton",holder)	box.Size=UDim2.new(0,28,0,28)	box.Position=UDim2.new(0,0,0.5,-14)	box.BackgroundColor3=Color3.fromRGB(40,40,40)	Instance.new("UICorner",box).CornerRadius=UDim.new(0,6)	rainbowStroke(box)	local lbl=Instance.new("TextLabel",holder)	lbl.Size=UDim2.new(1,-35,1,0)	lbl.Position=UDim2.new(0,35,0,0)	lbl.BackgroundTransparency=1	lbl.Text=text	lbl.Font=Enum.Font.GothamBold	lbl.TextSize=14	lbl.TextXAlignment=Enum.TextXAlignment.Left	rainbowText(lbl)	local state=false	box.MouseButton1Click:Connect(function()		state = not state		box.BackgroundColor3 = state and Color3.fromRGB(0,200,0) or Color3.fromRGB(40,40,40)		callback(state)	end)end-- THÊM NÚT DEMOcreateButton(tab1,"Click Me",function() print("Demo1 clicked") end)createToggle(tab1,"Toggle Me",function(s) print("Toggle state:",s) end)createButton(tab2,"Click Me 2",function() print("Demo2 clicked") end)createToggle(tab2,"Toggle Me 2",function(s) print("Toggle state 2:",s) end)