--// LOAD FLUENTlocal Fluent = loadstring(game:HttpGet("https://github.com/dawid-scripts/Fluent/releases/latest/download/main.lua"))()local SaveManager = loadstring(game:HttpGet("https://raw.githubusercontent.com/dawid-scripts/Fluent/master/Addons/SaveManager.lua"))()local InterfaceManager = loadstring(game:HttpGet("https://raw.githubusercontent.com/dawid-scripts/Fluent/master/Addons/InterfaceManager.lua"))()local Window = Fluent:CreateWindow({    Title = "Fluent Rainbow Demo",    SubTitle = "by PhucMax",    TabWidth = 160,    Size = UDim2.fromOffset(580, 460),    Acrylic = true,    Theme = "Dark",    MinimizeKey = Enum.KeyCode.LeftControl})-- Rainbow gradient stroke cho UIlocal function createRainbowStroke(frame)    local stroke = Instance.new("UIStroke", frame)    stroke.Thickness = 2    stroke.ApplyStrokeMode = Enum.ApplyStrokeMode.Border    task.spawn(function()        local hue = 0        while frame.Parent do            hue = (hue + 1) % 360            stroke.Color = Color3.fromHSV(hue/360, 1, 1)            task.wait(0.03)        end    end)endcreateRainbowStroke(Window.MainFrame)-- Rainbow gradient text cho UItask.spawn(function()    while true do        local hue = tick() % 360 / 360        local color = Color3.fromHSV(hue, 1, 1)        for _, v in pairs(Window.MainFrame:GetDescendants()) do            if v:IsA("TextLabel") or v:IsA("TextButton") then                v.TextColor3 = color            end        end        task.wait(0.03)    endend)-- Nút bật/tắt UI trên đỉnh giữa màn hìnhlocal toggleBtn = Instance.new("TextButton", game.CoreGui)toggleBtn.Size = UDim2.new(0, 100, 0, 36)toggleBtn.Position = UDim2.new(0.5, -50, 0, 10)toggleBtn.Text = "UI"toggleBtn.Font = Enum.Font.GothamBoldtoggleBtn.TextSize = 18toggleBtn.BackgroundColor3 = Color3.fromRGB(30,30,30)toggleBtn.Active = truetoggleBtn.Draggable = truelocal corner = Instance.new("UICorner", toggleBtn)corner.CornerRadius = UDim.new(0,8)local stroke = Instance.new("UIStroke", toggleBtn)stroke.Thickness = 2task.spawn(function()    local hue = 0    while toggleBtn.Parent do        hue = (hue + 1) % 360        local color = Color3.fromHSV(hue/360,1,1)        toggleBtn.TextColor3 = color        stroke.Color = color        task.wait(0.03)    endend)-- Animation bật/tắt UItoggleBtn.MouseButton1Click:Connect(function()    if Window.MainFrame.Visible then        local tween = game:GetService("TweenService"):Create(Window.MainFrame, TweenInfo.new(0.2, Enum.EasingStyle.Quad), {            Size = UDim2.fromOffset(0,0)        })        tween:Play()        tween.Completed:Wait()        Window.MainFrame.Visible = false    else        Window.MainFrame.Visible = true        Window.MainFrame.Size = UDim2.fromOffset(0,0)        local tween = game:GetService("TweenService"):Create(Window.MainFrame, TweenInfo.new(0.3, Enum.EasingStyle.Quad), {            Size = UDim2.fromOffset(580,460)        })        tween:Play()    endend)--// TẠO TABlocal Tabs = {    Demo1 = Window:AddTab({Title = "Demo1"}),    Demo2 = Window:AddTab({Title = "Demo2"})}-- Demo button cho từng tabTabs.Demo1:AddButton({    Title = "Click Me 1",    Description = "Demo button tab 1",    Callback = function()        print("Button Demo1 clicked!")    end})Tabs.Demo2:AddButton({    Title = "Click Me 2",    Description = "Demo button tab 2",    Callback = function()        print("Button Demo2 clicked!")    end})-- AddonsSaveManager:SetLibrary(Fluent)InterfaceManager:SetLibrary(Fluent)SaveManager:IgnoreThemeSettings()InterfaceManager:SetFolder("FluentDemoHub")SaveManager:SetFolder("FluentDemoHub/specific-game")InterfaceManager:BuildInterfaceSection(Tabs.Demo2)SaveManager:BuildConfigSection(Tabs.Demo2)Window:SelectTab(1)Fluent:Notify({Title="Fluent Rainbow", Content="Demo UI Loaded!", Duration=5})