-- PHUCMAX AutoFruit module (START / STOP ch·∫Øc ch·∫Øn)if getgenv()._phucmax_auto_loaded then return endgetgenv()._phucmax_auto_loaded = true--// Serviceslocal Players = game:GetService("Players")local RunService = game:GetService("RunService")local HttpService = game:GetService("HttpService")local TeleportService = game:GetService("TeleportService")local Workspace = game:GetService("Workspace")local CoreGui = game:GetService("CoreGui")local LocalPlayer = Players.LocalPlayer--// GLOBAL FLAGS (m·∫∑c ƒë·ªãnh b·∫≠t)getgenv().ScriptRunning = true      -- master flaggetgenv().AutoFruit = true         -- t√¨m + nh·∫∑t tr√°igetgenv().AutoHop = true           -- hop server khi ko c√≥ tr√°igetgenv().CheckDelay = 0.5         -- check nhanhgetgenv().HopDelay = 4             -- ƒë·ª£i 4s tr∆∞·ªõc khi hop--// Internal trackerslocal Connections = {}            -- l∆∞u k·∫øt n·ªëi ƒë·ªÉ disconnectlocal Billboards = {}             -- l∆∞u billboard ƒë·ªÉ x√≥alocal joinedServers = { [game.JobId] = true }local fruitLoopRunning = false--// Helperslocal function trackConn(conn)    if conn and type(conn.Disconnect) == "function" then        table.insert(Connections, conn)    endendlocal function disconnectAll()    for _, conn in ipairs(Connections) do        pcall(function() conn:Disconnect() end)    end    Connections = {}endlocal function safeDestroy(inst)    pcall(function()        if inst and inst.Destroy then inst:Destroy() end    end)endlocal function clearBillboards()    for _, b in ipairs(Billboards) do        safeDestroy(b)    end    Billboards = {}endlocal function rainbowColor(hueShift)    return Color3.fromHSV((tick()*0.28 + (hueShift or 0)) % 1, 1, 1)end--// ESP (Billboard) - attach to a BasePartlocal function createESP(part, text)    if not part or not part.Parent then return end    if part:FindFirstChild("FruitESP") then        local gui = part:FindFirstChild("FruitESP")        local lbl = gui:FindFirstChild("Label")        if lbl then return lbl end    end    local billboard = Instance.new("BillboardGui")    billboard.Name = "FruitESP"    billboard.Adornee = part    billboard.AlwaysOnTop = true    billboard.Size = UDim2.new(0,200,0,40)    billboard.StudsOffset = Vector3.new(0,2,0)    local label = Instance.new("TextLabel")    label.Name = "Label"    label.Size = UDim2.new(1,0,1,0)    label.BackgroundTransparency = 1    label.Text = tostring(text or "Fruit")    label.Font = Enum.Font.GothamBold    label.TextScaled = true    label.TextColor3 = rainbowColor(0)    label.Parent = billboard    billboard.Parent = part    table.insert(Billboards, billboard)    -- update m√†u rainbow, t·ª± disconnect khi script stop    local conn = RunService.RenderStepped:Connect(function()        if not getgenv().ScriptRunning then            pcall(function() conn:Disconnect() end)            return        end        if label and label.Parent then            label.TextColor3 = rainbowColor(0)        end    end)    trackConn(conn)    return labelend--// Server hop (flood-safe: t√¨m server ch∆∞a v√†o, teleport)local function serverHop()    if not getgenv().ScriptRunning then return end    local cursor = ""    while getgenv().ScriptRunning do        local ok, raw = pcall(function()            local url = string.format("https://games.roblox.com/v1/games/%d/servers/Public?sortOrder=Asc&limit=100%s",                game.PlaceId,                cursor ~= "" and "&cursor=" .. cursor or ""            )            return game:HttpGet(url)        end)        if not ok or not raw then return end        local ok2, data = pcall(function() return HttpService:JSONDecode(raw) end)        if not ok2 or type(data) ~= "table" then return end        if data.data then            for _, s in ipairs(data.data) do                if not getgenv().ScriptRunning then return end                if type(s) == "table" and s.id and s.id ~= game.JobId and s.playing < s.maxPlayers then                    if not joinedServers[s.id] then                        joinedServers[s.id] = true                        pcall(function()                            TeleportService:TeleportToPlaceInstance(game.PlaceId, s.id, LocalPlayer)                        end)                        return                    end                end            end        end        if not data.nextPageCursor then break end        cursor = data.nextPageCursor    endend--// Core fruit loop (check nhanh, teleport, esp, hop after delay)local function startFruitLoop()    if fruitLoopRunning then return end    fruitLoopRunning = true    task.spawn(function()        while getgenv().ScriptRunning do            local foundFruit = false            if getgenv().AutoFruit then                -- scan descendants quickly (catch Tools in containers)                for _, obj in ipairs(Workspace:GetDescendants()) do                    if not getgenv().ScriptRunning then break end                    if obj:IsA("Tool") and obj:FindFirstChild("Handle") then                        foundFruit = true                        -- create esp and teleport                        createESP(obj.Handle, obj.Name)                        pcall(function()                            local hrp = LocalPlayer.Character and LocalPlayer.Character:FindFirstChild("HumanoidRootPart")                            if hrp and obj.Handle and obj.Handle.Position then                                -- d√πng CFrame (nhanh & √≠t d√≠nh MoveTo issues)                                hrp.CFrame = CFrame.new(obj.Handle.Position + Vector3.new(0,5,0))                            end                        end)                        -- ƒë·ª£i 1s ƒë·ªÉ nh·∫∑t                        local t = 0                        while t < 1 and getgenv().ScriptRunning do                            task.wait(0.1); t = t + 0.1                        end                    end                end            end            if not found and getgenv().AutoHop and getgenv().ScriptRunning then                -- ch·ªù HopDelay nh∆∞ng re-check li√™n t·ª•c, n·∫øu xu·∫•t hi·ªán fruit th√¨ h·ªßy hop                local waited = 0                local willHop = true                while waited < getgenv().HopDelay and getgenv().ScriptRunning do                    -- re-check presence                    local any = false                    for _, o in ipairs(Workspace:GetDescendants()) do                        if o:IsA("Tool") and o:FindFirstChild("Handle") then                            any = true; break                        end                    end                    if any then                        willHop = false                        break                    end                    task.wait(0.25); waited = waited + 0.25                end                if willHop and getgenv().ScriptRunning then                    -- hop server                    pcall(serverHop)                end            end            -- sleep small amount between scans            local delayT = getgenv().CheckDelay or 0.5            local slept = 0            while slept < delayT and getgenv().ScriptRunning do                task.wait(0.1); slept = slept + 0.1            end        end        fruitLoopRunning = false    end)end--// STOP/START + cleanuplocal function StopAll()    getgenv().ScriptRunning = false    getgenv().AutoFruit = false    getgenv().AutoHop = false    -- disconnect connections & clear billboards    disconnectAll()    clearBillboards()    -- destroy known UI (keeps stop button)    pcall(function()        if CoreGui:FindFirstChild("PHUCMAX_UI") then CoreGui.PHUCMAX_UI:Destroy() end    end)    warn("[PHUCMAX] All stopped")endlocal function StartAll()    if getgenv().ScriptRunning then return end    -- reset flags (m·∫∑c ƒë·ªãnh b·∫≠t auto)    getgenv().ScriptRunning = true    getgenv().AutoFruit = true    getgenv().AutoHop = true    -- start loops    startFruitLoop()    warn("[PHUCMAX] Started")end--// UI STOP/START (gi·ªØa b√™n tr√°i, h∆°i cao)-- x√≥a GUI c≈© n·∫øu c√≥if CoreGui:FindFirstChild("PHUCMAX_STOPBTN") then    pcall(function() CoreGui.PHUCMAX_STOPBTN:Destroy() end)endlocal stopGui = Instance.new("ScreenGui")stopGui.Name = "PHUCMAX_STOPBTN"stopGui.ResetOnSpawn = falsestopGui.Parent = CoreGuilocal btn = Instance.new("TextButton")btn.Parent = stopGuibtn.Size = UDim2.new(0,160,0,48)btn.Position = UDim2.new(0,20,0.35,0) -- cao h∆°n gi·ªØabtn.AnchorPoint = Vector2.new(0,0)btn.Text = "‚õî STOP"btn.Font = Enum.Font.GothamBoldbtn.TextSize = 18btn.TextColor3 = Color3.new(1,1,1)btn.BackgroundTransparency = 0btn.BackgroundColor3 = Color3.fromRGB(30,30,30)local corner = Instance.new("UICorner", btn); corner.CornerRadius = UDim.new(0,12)local grad = Instance.new("UIGradient", btn)grad.Color = ColorSequence.new{    ColorSequenceKeypoint.new(0, Color3.fromRGB(255,0,0)),    ColorSequenceKeypoint.new(0.2, Color3.fromRGB(255,255,0)),    ColorSequenceKeypoint.new(0.4, Color3.fromRGB(0,255,0)),    ColorSequenceKeypoint.new(0.6, Color3.fromRGB(0,255,255)),    ColorSequenceKeypoint.new(0.8, Color3.fromRGB(0,0,255)),    ColorSequenceKeypoint.new(1, Color3.fromRGB(255,0,255))}task.spawn(function()    while true do        if not grad or not btn then break end        grad.Rotation = (grad.Rotation + 3) % 360        task.wait(0.03)    endend)-- click toggles stop/startbtn.MouseButton1Click:Connect(function()    if getgenv().ScriptRunning then        StopAll()        btn.Text = "‚ñ∂Ô∏è START"        btn.BackgroundColor3 = Color3.fromRGB(80,80,80)    else        -- if stopped, start loops in-place        StartAll()        btn.Text = "‚õî STOP"        btn.BackgroundColor3 = Color3.fromRGB(30,30,30)    endend)-- Auto start once when injected (since globals default to true)startFruitLoop()--// Serviceslocal Players = game:GetService("Players")local RunService = game:GetService("RunService")local Stats = game:GetService("Stats")local TeleportService = game:GetService("TeleportService")local HttpService = game:GetService("HttpService")local ReplicatedStorage = game:GetService("ReplicatedStorage")local Workspace = game:GetService("Workspace")local LocalPlayer = Players.LocalPlayer--// UI PHUCMAXlocal screenGui = Instance.new("ScreenGui", game.CoreGui)screenGui.Name = "PHUCMAX_UI"screenGui.ZIndexBehavior = Enum.ZIndexBehavior.Globallocal mainFrame = Instance.new("Frame", screenGui)mainFrame.Size = UDim2.new(1,0,1,0)mainFrame.BackgroundTransparency = 1local uiLayout = Instance.new("UIListLayout", mainFrame)uiLayout.HorizontalAlignment = Enum.HorizontalAlignment.CenteruiLayout.VerticalAlignment = Enum.VerticalAlignment.CenteruiLayout.Padding = UDim.new(0,12)local function createRainbowText(text,size)    local label = Instance.new("TextLabel")    label.Size = UDim2.new(0,500,0,size+20)    label.BackgroundTransparency = 1    label.Text = text    label.Font = Enum.Font.GothamBold    label.TextSize = size    label.TextColor3 = Color3.fromRGB(255,255,255)    label.ZIndex = 10    local gradient = Instance.new("UIGradient",label)    gradient.Rotation = 0    gradient.Color = ColorSequence.new{        ColorSequenceKeypoint.new(0, Color3.fromRGB(255,0,0)),        ColorSequenceKeypoint.new(0.2, Color3.fromRGB(255,255,0)),        ColorSequenceKeypoint.new(0.4, Color3.fromRGB(0,255,0)),        ColorSequenceKeypoint.new(0.6, Color3.fromRGB(0,255,255)),        ColorSequenceKeypoint.new(0.8, Color3.fromRGB(0,0,255)),        ColorSequenceKeypoint.new(1, Color3.fromRGB(255,0,255))    }    task.spawn(function()        while task.wait(0.03) do            gradient.Rotation = (gradient.Rotation+3)%360        end    end)    return labelendlocal title = createRainbowText("PHUCMAX",50); title.Parent = mainFramelocal subtitle = createRainbowText("nhattrai",30); subtitle.Parent = mainFramelocal fpsLabel = createRainbowText("FPS: 0",25); fpsLabel.Parent = mainFramelocal pingLabel = createRainbowText("Ping: 0 ms",25); pingLabel.Parent = mainFrame--// Update FPS + Pinglocal frames,lastTime=0,tick()RunService.RenderStepped:Connect(function()    frames+=1    local currentTime = tick()    if currentTime-lastTime>=1 then        fpsLabel.Text="FPS: "..frames        frames=0        lastTime=currentTime    endend)RunService.Heartbeat:Connect(function()    local pingStat = Stats.Network.ServerStatsItem["Data Ping"]    if pingStat then        pingLabel.Text="Ping: "..math.floor(pingStat:GetValue()).." ms"    endend)--// Th√¥ng b√°o g√≥c ph·∫£i d∆∞·ªõilocal notifyFrame = Instance.new("Frame",screenGui)notifyFrame.Size = UDim2.new(0,300,0,200)notifyFrame.Position = UDim2.new(1,-310,1,-260)notifyFrame.BackgroundTransparency = 1local notifyLayout = Instance.new("UIListLayout",notifyFrame)notifyLayout.SortOrder = Enum.SortOrder.LayoutOrdernotifyLayout.Padding = UDim.new(0,5)notifyLayout.VerticalAlignment = Enum.VerticalAlignment.Bottomlocal function Notify(msg)    local note = createRainbowText(msg,20)    note.Parent = notifyFrame    note.Size = UDim2.new(1,0,0,25)    task.delay(2,function() note:Destroy() end)end--// Th√™m ch·ªØ Discord ƒë·∫ßu m√†n h√¨nhlocal function createDiscordText(text, size, link)    local button = Instance.new("TextButton")    button.Size = UDim2.new(0, 300, 0, size + 10)    button.Position = UDim2.new(0.5, -150, 0, 0) -- gi·ªØa tr√™n c√πng    button.BackgroundTransparency = 1    button.Text = text    button.Font = Enum.Font.GothamBold    button.TextSize = size    button.TextColor3 = Color3.fromRGB(255, 255, 255)    button.ZIndex = 20    button.Parent = screenGui    local gradient = Instance.new("UIGradient", button)    gradient.Rotation = 0    gradient.Color = ColorSequence.new{        ColorSequenceKeypoint.new(0, Color3.fromRGB(255,0,0)),        ColorSequenceKeypoint.new(0.2, Color3.fromRGB(255,255,0)),        ColorSequenceKeypoint.new(0.4, Color3.fromRGB(0,255,0)),        ColorSequenceKeypoint.new(0.6, Color3.fromRGB(0,255,255)),        ColorSequenceKeypoint.new(0.8, Color3.fromRGB(0,0,255)),        ColorSequenceKeypoint.new(1, Color3.fromRGB(255,0,255))    }    task.spawn(function()        while task.wait(0.03) do            gradient.Rotation = (gradient.Rotation + 3) % 360        end    end)    -- Khi click th√¨ m·ªü/copy link Discord    button.MouseButton1Click:Connect(function()        if syn and syn.request then            syn.request({Url = link, Method = "GET"})        elseif request then            request({Url = link, Method = "GET"})        else            setclipboard(link)            game.StarterGui:SetCore("SendNotification", {                Title = "PHUCMAX Hub",                Text = "ƒê√£ copy link Discord v√†o clipboard!",                Duration = 5            })        end    end)    return buttonend--// Th√™m v√†o UIlocal discordBtn = createDiscordText(" Discord https://discord.gg/a7rtPheBY6 ", 25, "")--// Auto Join Marinestask.spawn(function()    pcall(function()        local TeamBtn = LocalPlayer.PlayerGui:WaitForChild("ChooseTeam").Container.Marines.Frame.ViewportFrame.TextButton        fireclickdetector(TeamBtn.ClickDetector)        Notify("‚öì Auto Join Marines")    end)end)local function FixLagX2()    -- Gi·∫£m chi ti·∫øt c∆° b·∫£n    for _, obj in ipairs(workspace:GetDescendants()) do        if obj:IsA("BasePart") then            obj.Material = Enum.Material.SmoothPlastic            obj.Color = Color3.fromRGB(150,150,150)            obj.Reflectance = 0        elseif obj:IsA("ParticleEmitter") or obj:IsA("Trail") or obj:IsA("Beam") then            obj.Enabled = false        end    end    -- Th√™m X2    for _, obj in ipairs(workspace:GetDescendants()) do        if obj.Name:match("Leaf") or obj.Name:match("Tree") then            obj:Destroy()        elseif obj:IsA("UnionOperation") or obj:IsA("MeshPart") then            obj.Transparency = 0.9        end    end    -- Gi·∫£m √°nh s√°ng    local Lighting = game:GetService("Lighting")    Lighting.GlobalShadows = false    Lighting.Brightness = Lighting.Brightness * 0.3    Notify(" ‚úÖ FixLag X2 applied")    print("‚úÖ FixLag X2 applied")end-- T·ª± ƒë·ªông ch·∫°y khi script loadtask.spawn(FixLagX2)--// Auto Store Fruitgetgenv().AutoStoreFruit = truelocal FRUIT_MAP = {    ["Bomb Fruit"]={remote="StoreFruit",arg="Bomb-Bomb"},    ["Spike Fruit"]={remote="StoreFruit",arg="Spike-Spike"},    ["Chop Fruit"]={remote="StoreFruit",arg="Chop-Chop"},    ["Spring Fruit"]={remote="StoreFruit",arg="Spring-Spring"},    ["Kilo Fruit"]={remote="StoreFruit",arg="Rocket-Rocket"},    ["Rocket Fruit"]={remote="StoreFruit",arg="Rocket-Rocket"},    ["Smoke Fruit"]={remote="StoreFruit",arg="Smoke-Smoke"},    ["Spin Fruit"]={remote="StoreFruit",arg="Spin-Spin"},    ["Flame Fruit"]={remote="StoreFruit",arg="Flame-Flame"},    ["Falcon Fruit"]={remote="StoreFruit",arg="Falcon"},    ["Ice Fruit"]={remote="StoreFruit",arg="Ice-Ice"},    ["Sand Fruit"]={remote="StoreFruit",arg="Sand-Sand"},    ["Dark Fruit"]={remote="StoreFruit",arg="Dark-Dark"},    ["Revive Fruit"]={remote="StoreFruit",arg="Ghost-Ghost"},    ["Ghost Fruit"]={remote="StoreFruit",arg="Ghost-Ghost"},    ["Diamond Fruit"]={remote="StoreFruit",arg="Diamond-Diamond"},    ["Light Fruit"]={remote="StoreFruit",arg="Light-Light"},    ["Love Fruit"]={remote="StoreFruit",arg="Love-Love"},    ["Rubber Fruit"]={remote="StoreFruit",arg="Rubber-Rubber"},    ["Barrier Fruit"]={remote="StoreFruit",arg="Barrier-Barrier"},    ["Magma Fruit"]={remote="StoreFruit",arg="Magma-Magma"},    ["Portal Fruit"]={remote="StoreFruit",arg="Door-Door"},    ["Quake Fruit"]={remote="StoreFruit",arg="Quake-Quake"},    ["Buddha Fruit"]={remote="Buddha",arg=nil},    ["Spider Fruit"]={remote="StoreFruit",arg="Spider-Spider"},    ["Phoenix Fruit"]={remote="StoreFruit",arg="Phoenix"},    ["Rumble Fruit"]={remote="StoreFruit",arg="Rumble-Rumble"},    ["Pain Fruit"]={remote="StoreFruit",arg="Pain-Pain"},    ["Gravity Fruit"]={remote="StoreFruit",arg="Gravity-Gravity"},    ["Dough Fruit"]={remote="StoreFruit",arg="Dough-Dough"},    ["Shadow Fruit"]={remote="StoreFruit",arg="Shadow-Shadow"},    ["Venom Fruit"]={remote="StoreFruit",arg="Venom-Venom"},    ["Eagle Fruit"]={remote="StoreFruit",arg="Eagle-Eagle"},    ["creation Fruit"]={remote="StoreFruit",arg="creation-creation"},    ["Lightning Fruit"]={remote="StoreFruit",arg="Lightning-Lightning"},    ["Portal Fruit"]={remote="StoreFruit",arg="Portal-Portal"},    ["Mammoth Fruit"]={remote="StoreFruit",arg="Mammoth-Mammoth"},    ["T-Rex Fruit"]={remote="StoreFruit",arg="T-Rex-T-Rex"},    ["Gas Fruit"]={remote="StoreFruit",arg="Gas-Gas"},    ["Yeti Fruit"]={remote="StoreFruit",arg="Yeti-Yeti"},    ["Kitsune Fruit"]={remote="StoreFruit",arg="Kitsune-Kitsune"},    ["Blade Fruit"]={remote="StoreFruit",arg="Blade-Blade"},    ["Control Fruit"]={remote="StoreFruit",arg="Control-Control"},    ["Spirit Fruit"]={remote="StoreFruit",arg="Soul-Soul"},    ["Dragon Fruit"]={remote="StoreFruit",arg="Dragon-Dragon"},    ["Leopard Fruit"]={remote="StoreFruit",arg="Leopard-Leopard"},}local lastStoreTime = {}local COOLDOWN_PER_FRUIT = 2local function callRemoteStore(remoteName,arg,fruitInstance)    local remoteObj = ReplicatedStorage:FindFirstChild(remoteName) or (ReplicatedStorage:FindFirstChild("Remotes") and ReplicatedStorage.Remotes:FindFirstChild(remoteName))    if not remoteObj then return false,"RemoteNotFound" end    local ok,res=pcall(function()        if remoteName=="StoreFruit" then            return remoteObj:InvokeServer("StoreFruit",arg,fruitInstance)        else            if arg then                return remoteObj:InvokeServer(arg,fruitInstance)            else                return remoteObj:InvokeServer(fruitInstance)            end        end    end)    return ok,resendlocal function checkAndStoreAll()    local player=LocalPlayer    local backpack = player:FindFirstChild("Backpack")    local char = player.Character    for fruitName,info in pairs(FRUIT_MAP) do        local last = lastStoreTime[fruitName] or 0        if tick()-last>=COOLDOWN_PER_FRUIT then            local instanceInChar = char and char:FindFirstChild(fruitName) or            nil            local instanceInBackpack = backpack and backpack:FindFirstChild(fruitName) or nil            local foundInst = instanceInChar or instanceInBackpack            if foundInst then                local ok,res = pcall(function()                    if ReplicatedStorage:FindFirstChild("Remotes") and ReplicatedStorage.Remotes:FindFirstChild("CommF_") and info.remote=="StoreFruit" then                        return ReplicatedStorage.Remotes.CommF_:InvokeServer("StoreFruit",info.arg,foundInst)                    else                        return callRemoteStore(info.remote,info.arg,foundInst)                    end                end)                lastStoreTime[fruitName] = tick()                if ok then                    Notify("Stored "..fruitName)                else                    Notify("Failed "..fruitName)                end            end        end    endendspawn(function()    while true do        if getgenv().AutoStoreFruit then            pcall(checkAndStoreAll)            task.wait(0.6)        else            task.wait(0.8)        end    endend)--// Auto Fruit Finder + ESP + Teleport + Auto Hop_G.AutoFruit = true_G.AutoHop = truelocal joinedServers = {}joinedServers[game.JobId] = truelocal function rainbowColor(hueShift)    return Color3.fromHSV((tick()*0.3 + hueShift)%1,1,1)endlocal function teleportTo(pos)    if LocalPlayer.Character and LocalPlayer.Character:FindFirstChild("HumanoidRootPart") then        LocalPlayer.Character:MoveTo(pos)    endendlocal function createESP(obj,text)    if obj:FindFirstChild("FruitESP") then return obj.FruitESP.TextLabel end    local billboard = Instance.new("BillboardGui")    billboard.Name = "FruitESP"    billboard.Adornee = obj    billboard.AlwaysOnTop = true    billboard.Size = UDim2.new(0,200,0,50)    billboard.StudsOffset = Vector3.new(0,2,0)    local label = Instance.new("TextLabel")    label.Size = UDim2.new(1,0,1,0)    label.BackgroundTransparency = 1    label.Text = text    label.Font = Enum.Font.SourceSansBold    label.TextScaled = true    label.TextColor3 = Color3.fromRGB(255,255,255)    label.Parent = billboard    billboard.Parent = obj    RunService.RenderStepped:Connect(function()        if label then            label.TextColor3 = rainbowColor(0)        end    end)    return labelendlocal function serverHop()    local cursor = ""    while true do        local url = string.format("https://games.roblox.com/v1/games/%d/servers/Public?sortOrder=Asc&limit=100%s",            game.PlaceId,            cursor~="" and "&cursor="..cursor or "")        local req = HttpService:JSONDecode(game:HttpGet(url))        if req and req.data then            for _,s in pairs(req.data) do                if type(s)=="table" and s.id~=game.JobId and s.playing<s.maxPlayers then                    if not joinedServers[s.id] then                        joinedServers[s.id]=true                        TeleportService:TeleportToPlaceInstance(game.PlaceId,s.id,LocalPlayer)                        return                    end                end            end        end        if not req.nextPageCursor then break end        cursor=req.nextPageCursor    endendtask.spawn(function()    while task.wait(0.5) do -- check nhanh h∆°n (0.5 gi√¢y 1 l·∫ßn)        if _G.AutoFruit then            local foundFruit = false            for _,fruit in pairs(Workspace:GetChildren()) do                if fruit:IsA("Tool") and fr