-- üìå PHUCMAX HUB ‚Äì FULL SCRIPT-- Part 1/3: UI + Rainbow Text + Notification System-- Serviceslocal Players = game:GetService("Players")local RunService = game:GetService("RunService")local HttpService = game:GetService("HttpService")local TweenService = game:GetService("TweenService")local Lighting = game:GetService("Lighting")local LocalPlayer = Players.LocalPlayerlocal PlayerGui = LocalPlayer:WaitForChild("PlayerGui")-- Rainbow gradient functionlocal function rainbowColor(hueShift)    return Color3.fromHSV((tick() * 0.2 + hueShift) % 1, 1, 1)end-- Main ScreenGuilocal MainUI = Instance.new("ScreenGui")MainUI.Name = "PHUCMAX_UI"MainUI.ResetOnSpawn = falseMainUI.Parent = PlayerGui-- Containerlocal Container = Instance.new("Frame")Container.Size = UDim2.new(1, 0, 1, 0)Container.BackgroundTransparency = 1Container.Parent = MainUI-- Title PHUCMAXlocal Title = Instance.new("TextLabel")Title.Text = "PHUCMAX"Title.Font = Enum.Font.GothamBoldTitle.TextScaled = trueTitle.Size = UDim2.new(0.4, 0, 0.15, 0)Title.Position = UDim2.new(0.3, 0, 0.05, 0)Title.BackgroundTransparency = 1Title.Parent = Container-- SubText nhattrailocal SubText = Instance.new("TextLabel")SubText.Text = "nhattrai"SubText.Font = Enum.Font.GothamSemiboldSubText.TextScaled = trueSubText.Size = UDim2.new(0.25, 0, 0.1, 0)SubText.Position = UDim2.new(0.375, 0, 0.18, 0)SubText.BackgroundTransparency = 1SubText.Parent = Container-- FPS + Pinglocal Status = Instance.new("TextLabel")Status.Text = "FPS: 0 | Ping: 0"Status.Font = Enum.Font.GothamStatus.TextScaled = trueStatus.Size = UDim2.new(0.3, 0, 0.08, 0)Status.Position = UDim2.new(0.35, 0, 0.28, 0)Status.BackgroundTransparency = 1Status.Parent = Container-- Notifications containerlocal NotiHolder = Instance.new("Frame")NotiHolder.Size = UDim2.new(0.25, 0, 0.6, 0)NotiHolder.Position = UDim2.new(0.75, 0, 0.35, 0)NotiHolder.BackgroundTransparency = 1NotiHolder.Parent = MainUI-- Function create notificationlocal function createNotification(msg)    local Noti = Instance.new("TextLabel")    Noti.Text = msg    Noti.Font = Enum.Font.GothamBold    Noti.TextScaled = true    Noti.Size = UDim2.new(1, 0, 0.08, 0)    Noti.BackgroundTransparency = 1    Noti.Parent = NotiHolder    -- Position bottom then tween up    Noti.Position = UDim2.new(0, 0, 1, 0)    local tweenIn = TweenService:Create(Noti, TweenInfo.new(0.3), {Position = UDim2.new(0, 0, 0.9, 0)})    tweenIn:Play()    task.delay(2, function()        local tweenOut = TweenService:Create(Noti, TweenInfo.new(0.3), {Position = UDim2.new(0, 0, 1.1, 0)})        tweenOut:Play()        tweenOut.Completed:Wait()        Noti:Destroy()    end)    -- Rainbow effect    task.spawn(function()        while Noti.Parent do            Noti.TextColor3 = rainbowColor(0)            RunService.RenderStepped:Wait()        end    end)end-- Rainbow update looptask.spawn(function()    while task.wait() do        Title.TextColor3 = rainbowColor(0)        SubText.TextColor3 = rainbowColor(0.2)        Status.TextColor3 = rainbowColor(0.4)        -- Update FPS + Ping        local fps = math.floor(1 / RunService.RenderStepped:Wait())        local ping = game:GetService("Stats").Network.ServerStatsItem["Data Ping"]:GetValueString()        Status.Text = string.format("FPS: %d | Ping: %s", fps, ping)    endend)-- Test notificationcreateNotification("‚úÖ UI Loaded th√†nh c√¥ng")-- Part 2/3: Ch·ª©c nƒÉng ch√≠nh PHUCMAX--// Auto Join Marinestask.spawn(function()    pcall(function()        local chooseTeam = LocalPlayer:WaitForChild("PlayerGui"):WaitForChild("ChooseTeam")        local teamBtn = chooseTeam.Container.Marines.Frame.ViewportFrame.TextButton        if teamBtn then            fireclickdetector(teamBtn.ClickDetector)            createNotification("‚öì Auto Join Marines")        end    end)end)--// Highlight & nh·∫∑t tr√°i t·ª± ƒë·ªônglocal function highlightFruit(fruit)    local handle = fruit:FindFirstChildWhichIsA("BasePart") or fruit:FindFirstChild("Handle")    if not handle then return end    local bill = Instance.new("BillboardGui")    bill.Size = UDim2.new(0, 100, 0, 20)    bill.Adornee = handle    bill.AlwaysOnTop = true    bill.Parent = fruit    local label = Instance.new("TextLabel")    label.Text = fruit.Name    label.Size = UDim2.new(1,0,1,0)    label.BackgroundTransparency = 1    label.Font = Enum.Font.GothamBold    label.TextScaled = true    label.TextColor3 = rainbowColor(0)    label.Parent = bill    -- Rainbow effect    task.spawn(function()        while label.Parent do            label.TextColor3 = rainbowColor(0.3)            RunService.RenderStepped:Wait()        end    end)end-- Auto pick fruit + ESP rainbowtask.spawn(function()    while task.wait(5) do        for _, obj in ipairs(workspace:GetDescendants()) do            if obj:IsA("Tool") and obj:FindFirstChild("Handle") and obj.Name:lower():find("fruit") then                highlightFruit(obj)                if LocalPlayer.Character and LocalPlayer.Character:FindFirstChild("HumanoidRootPart") then                    local hrp = LocalPlayer.Character.HumanoidRootPart                    hrp.CFrame = obj.Handle.CFrame + Vector3.new(0, 3, 0)                    firetouchinterest(hrp, obj.Handle, 0)                    firetouchinterest(hrp, obj.Handle, 1)                    createNotification("üçé Nh·∫∑t: " .. obj.Name)                end            end        end    endend)--// Auto Store Fruit_G.AutoStoreFruit = true -- m·∫∑c ƒë·ªãnh b·∫≠ttask.spawn(function()    while task.wait(1) do        if _G.AutoStoreFruit and LocalPlayer.Backpack then            for _, fruit in ipairs(LocalPlayer.Backpack:GetChildren()) do                if fruit:IsA("Tool") and fruit.Name:find("Fruit") then                    pcall(function()                        game:GetService("ReplicatedStorage").Remotes.CommF_:InvokeServer("StoreFruit", fruit.Name:gsub(" ", "-"), fruit)                        createNotification("üíæ L∆∞u tr·ªØ: " .. fruit.Name)                    end)                end            end        end    endend)--// Auto Server Hop khi h·∫øt tr√°ilocal function serverHop()    pcall(function()        local servers = HttpService:JSONDecode(game:HttpGet("https://games.roblox.com/v1/games/"..game.PlaceId.."/servers/Public?sortOrder=Asc&limit=100"))        for _, v in pairs(servers.data) do            if v.playing < v.maxPlayers and v.id ~= game.JobId then                createNotification("üîÑ Server Hop...")                game:GetService("TeleportService"):TeleportToPlaceInstance(game.PlaceId, v.id, LocalPlayer)                break            end        end    end)endtask.spawn(function()    while task.wait(60) do        local hasFruit = false        for _, obj in ipairs(workspace:GetDescendants()) do            if obj:IsA("Tool") and obj.Name:lower():find("fruit") then                hasFruit = true                break            end        end        if not hasFruit then            serverHop()        end    endend)--// FixLag X2local function FixLagX2()    for _, obj in ipairs(workspace:GetDescendants()) do        if obj:IsA("BasePart") then            obj.Material = Enum.Material.SmoothPlastic            obj.Color = Color3.fromRGB(150,150,150)            obj.Reflectance = 0        elseif obj:IsA("ParticleEmitter") or obj:IsA("Trail") or obj:IsA("Beam") then            obj.Enabled = false        end        if obj.Name:match("Leaf") or obj.Name:match("Tree") then            obj:Destroy()        elseif obj:IsA("UnionOperation") or obj:IsA("MeshPart") then            obj.Transparency = 0.9        end    end    Lighting.GlobalShadows = false    Lighting.Brightness = Lighting.Brightness * 0.3    createNotification("‚úÖ FixLag X2 Applied")endtask.spawn(FixLagX2)-- Part 3/3: UI toggle & th√¥ng b√°o g√≥c ph·∫£i--// UI Menu Framelocal menuFrame = Instance.new("Frame", screenGui)menuFrame.Size = UDim2.new(0, 220, 0, 300)menuFrame.Position = UDim2.new(0, 10, 0.3, 0)menuFrame.BackgroundColor3 = Color3.fromRGB(30,30,30)menuFrame.BorderSizePixel = 0menuFrame.Visible = truemenuFrame.ClipsDescendants = truemenuFrame.ZIndex = 20menuFrame.AnchorPoint = Vector2.new(0,0)--// Titlelocal menuTitle = Instance.new("TextLabel", menuFrame)menuTitle.Text = "PHUCMAX HUB"menuTitle.Font = Enum.Font.GothamBoldmenuTitle.TextSize = 20menuTitle.TextColor3 = Color3.fromRGB(255,255,255)menuTitle.BackgroundTransparency = 1menuTitle.Size = UDim2.new(1,0,0,30)--// Layoutlocal menuLayout = Instance.new("UIListLayout", menuFrame)menuLayout.SortOrder = Enum.SortOrder.LayoutOrdermenuLayout.Padding = UDim.new(0,5)--// Ch·ª©c nƒÉng Togglelocal function addToggle(title, default, callback)    local toggleFrame = Instance.new("Frame", menuFrame)    toggleFrame.Size = UDim2.new(1, -10, 0, 30)    toggleFrame.BackgroundTransparency = 1    local lbl = Instance.new("TextLabel", toggleFrame)    lbl.Text = title    lbl.Font = Enum.Font.Gotham    lbl.TextSize = 16    lbl.TextColor3 = Color3.fromRGB(255,255,255)    lbl.BackgroundTransparency = 1    lbl.Size = UDim2.new(0.7, 0, 1, 0)    lbl.TextXAlignment = Enum.TextXAlignment.Left    local btn = Instance.new("TextButton", toggleFrame)    btn.Size = UDim2.new(0.3,0,1,0)    btn.Position = UDim2.new(0.7,0,0,0)    btn.Text = default and "ON" or "OFF"    btn.TextColor3 = default and Color3.fromRGB(0,255,0) or Color3.fromRGB(255,0,0)    btn.BackgroundColor3 = Color3.fromRGB(50,50,50)    btn.BorderSizePixel = 0    local state = default    btn.MouseButton1Click:Connect(function()        state = not state        btn.Text = state and "ON" or "OFF"        btn.TextColor3 = state and Color3.fromRGB(0,255,0) or Color3.fromRGB(255,0,0)        callback(state)        createNotification("‚ö° " .. title .. " " .. (state and "ON" or "OFF"))    end)end-- Th√™m toggle cho c√°c ch·ª©c nƒÉngaddToggle("Auto Join Marines", true, function(v) _G.AutoJoinMarines = v end)addToggle("Auto Pick Fruit", true, function(v) _G.AutoPickFruit = v end)addToggle("Auto Store Fruit", true, function(v) _G.AutoStoreFruit = v end)addToggle("Auto Server Hop", true, function(v) _G.AutoServerHop = v end)addToggle("FixLag X2", true, function(v)     if v then FixLagX2() end end)--// Ch·ª©c nƒÉng t·∫°o th√¥ng b√°o g√≥c ph·∫£ifunction createNotification(msg)    local note = createRainbowText(msg, 18)    note.Size = UDim2.new(0,250,0,25)    note.Position = UDim2.new(1, -260, 1, -120)    note.AnchorPoint = Vector2.new(0,0)    note.Parent = screenGui    task.spawn(function()        wait(2)        note:Destroy()    end)end