-- KIỂM TRA ĐÃ LOAD UI CHƯAif getgenv then	if getgenv()._phucmax_ui_loaded then return end	getgenv()._phucmax_ui_loaded = trueend-- DỊCH VỤlocal TweenService = game:GetService("TweenService")local CoreGui = game:GetService("CoreGui")local Players = game:GetService("Players")local LocalPlayer = Players.LocalPlayerlocal RunService = game:GetService("RunService")local Workspace = game:GetService("Workspace")-- GUI CHÍNHlocal gui = Instance.new("ScreenGui", CoreGui)gui.Name = "PhucmaxRainbowUI"gui.ResetOnSpawn = false-- HÀM RAINBOWlocal function applyRainbowText(obj)	task.spawn(function()		while obj.Parent do			for h=0,1,0.01 do				local color = Color3.fromHSV(h,1,1)				obj.TextColor3 = color				task.wait(0.03)			end		end	end)endlocal function applyRainbowStroke(obj)	local stroke = Instance.new("UIStroke", obj)	stroke.Thickness = 2	stroke.ApplyStrokeMode = Enum.ApplyStrokeMode.Border	task.spawn(function()		while obj.Parent do			for h=0,1,0.01 do				stroke.Color = Color3.fromHSV(h,1,1)				task.wait(0.03)			end		end	end)end-- FRAME CHÍNHlocal main = Instance.new("Frame", gui)main.Size = UDim2.new(0, 280, 0, 340)main.AnchorPoint = Vector2.new(0.5, 0.5)main.Position = UDim2.new(0.5, 0, 0.5, 0)main.BackgroundColor3 = Color3.fromRGB(25, 25, 25)main.ClipsDescendants = truemain.Visible = falsemain.Active = truemain.Draggable = trueInstance.new("UICorner", main).CornerRadius = UDim.new(0, 12)applyRainbowStroke(main)-- HIỆN MENU (có animation)local function toggleMenu()	main.Visible = true	main.Position = UDim2.new(0.5, 0, 0.5, 0)	main.Size = UDim2.new(0, 0, 0, 0)	local tween = TweenService:Create(main, TweenInfo.new(0.3, Enum.EasingStyle.Quad), {		Size = UDim2.new(0, 280, 0, 340)	})	tween:Play()end-- THANH TABlocal tabBar = Instance.new("ScrollingFrame", main)tabBar.Size = UDim2.new(1, -20, 0, 36)tabBar.Position = UDim2.new(0, 10, 0, 10)tabBar.ScrollBarThickness = 4tabBar.ScrollingDirection = Enum.ScrollingDirection.XtabBar.CanvasSize = UDim2.new(0, 0, 0, 0)tabBar.BackgroundTransparency = 1local tabLayout = Instance.new("UIListLayout", tabBar)tabLayout.FillDirection = Enum.FillDirection.HorizontaltabLayout.Padding = UDim.new(0, 5)tabLayout.SortOrder = Enum.SortOrder.LayoutOrderlocal function updateTabCanvas()	task.wait()	local totalWidth = 0	for _, child in pairs(tabBar:GetChildren()) do		if child:IsA("TextButton") then			totalWidth += child.Size.X.Offset + tabLayout.Padding.Offset		end	end	tabBar.CanvasSize = UDim2.new(0, totalWidth, 0, 0)end-- KHUNG NỘI DUNG TABlocal tabContainer = Instance.new("Frame", main)tabContainer.Size = UDim2.new(1, -20, 1, -60)tabContainer.Position = UDim2.new(0, 10, 0, 50)tabContainer.BackgroundTransparency = 1-- TẠO TABlocal tabs, currentTab = {}, nillocal function switchTab(name)	for i, v in pairs(tabs) do		v.Visible = (i == name)	endendlocal function createTab(name)	local btn = Instance.new("TextButton", tabBar)	btn.Size = UDim2.new(0, 80, 1, 0)	btn.Text = name	btn.Font = Enum.Font.GothamBold	btn.TextSize = 14	btn.BackgroundColor3 = Color3.fromRGB(40, 40, 40)	Instance.new("UICorner", btn).CornerRadius = UDim.new(0, 6)	applyRainbowText(btn)	applyRainbowStroke(btn)	local page = Instance.new("ScrollingFrame", tabContainer)	page.Size = UDim2.new(1, 0, 1, 0)	page.CanvasSize = UDim2.new(0, 0, 0, 0)	page.ScrollBarThickness = 4	page.AutomaticCanvasSize = Enum.AutomaticSize.Y	page.BackgroundTransparency = 1	local layout = Instance.new("UIListLayout", page)	layout.Padding = UDim.new(0, 6)	layout.SortOrder = Enum.SortOrder.LayoutOrder	layout.HorizontalAlignment = Enum.HorizontalAlignment.Center	tabs[name] = page	page.Visible = false	btn.MouseButton1Click:Connect(function()		switchTab(name)	end)	if not currentTab then		currentTab = name		page.Visible = true	end	updateTabCanvas()	return pageend-- NÚT BẬT/TẮT MENUlocal logo = Instance.new("ImageButton", gui)logo.Size = UDim2.new(0, 48, 0, 48)logo.Position = UDim2.new(0.5, -24, 0, 10)logo.BackgroundColor3 = Color3.fromRGB(30, 30, 30)logo.Image = "rbxassetid://131027676437850"logo.Draggable = trueInstance.new("UICorner", logo).CornerRadius = UDim.new(1, 0)applyRainbowStroke(logo)logo.MouseButton1Click:Connect(function()	if main.Visible then		local tween = TweenService:Create(main, TweenInfo.new(0.2), {			Size = UDim2.new(0, 0, 0, 0)		})		tween:Play()		tween.Completed:Wait()		main.Visible = false	else		toggleMenu()	endend)-- TẠO TAB DEMO VỚI NÚTlocal demoTab1 = createTab("Demo1")local demoTab2 = createTab("Demo2")local function createDemoButton(parent, text)	local btn = Instance.new("TextButton", parent)	btn.Size = UDim2.new(0.9, 0, 0, 32)	btn.Text = text	btn.Font = Enum.Font.GothamBold	btn.TextSize = 14	Instance.new("UICorner", btn).CornerRadius = UDim.new(0, 6)	applyRainbowText(btn)	applyRainbowStroke(btn)	btn.MouseButton1Click:Connect(function()		print(text .. " clicked!")	end)endcreateDemoButton(demoTab1, "Click Me 1")createDemoButton(demoTab2, "Click Me 2")-- Rainbow text cho logoapplyRainbowText(logo)-- Notificationgame.StarterGui:SetCore("SendNotification", {    Title = "PHUCMAX",    Text = "chào anh em nha",    Duration = 5,    Icon = "rbxassetid://131027676437850"})